import { React, useState } from 'react';
import axios from 'axios';
import ShortenUrlHelpers from '../helpers/ShortenUrlHelper.js';

const allShortenUrls = ShortenUrlHelpers.getShortenUrl();

console.log("allShortenUrls", allShortenUrls);

const UrlSorten = () => {
    const [shortenURLs, setShortenURLs] = useState(allShortenUrls || []);
    const [inputText, setInput] = useState(''); // '' is the initial state value
    const [errMsg, setErrMsg] = useState('');
    const [isCopy, setCopy] = useState('');
    const baseUrl = 'http://localhost:3000/';

    const handelSortenUrl = (e) => {
        const url = inputText;
        if (!url) {
            setErrMsg("Please, provide a valid url");
            destroyNotification(true, setErrMsg);
        } else {
            axios.post('http://localhost:4000/generateSortenUrl', { url }).then(res => {
                const { slug } = res.data;
                if (slug) {
                    setInput(baseUrl + slug);
                    setCopy(true);
                }
            });
        }
    };

    const destroyNotification = (isError, setErrMsg) => {
        console.log("errMsg", errMsg, "setErrMsg", setErrMsg);
        if (isError) {
            setTimeout(function () {
                setErrMsg('');
            }, 2000);
        }
    };

    return (
        <div className="container">
            <div><h1 className="page-title">URL Sorten</h1></div>
            <div className="form-row align-items-center">
                <div className="col-auto">
                    <input type="text" className="form-control sorten-link" placeholder="Shorten your link" onInput={e => setInput(e.target.value)} value={inputText} />
                    {isCopy ? (
                        <button className="btn btn-primary" onClick={handelSortenUrl}>Copy</button>
                    ) : (
                        <button className="btn btn-primary" onClick={handelSortenUrl}>Sorten</button>
                    )}
                </div>
                {errMsg &&
                    <div className="alert alert-danger mt-20" role="alert">
                        {errMsg}
                    </div>
                }
                <div className="card mt-20">
                    <div className="card-body">
                        {shortenURLs.map((shortenURL, index) => (
                            <div className="row url-row" key={index}>
                                <div className="col-sm-6">{shortenURL.url}</div>
                                <div className="col-sm-6 text-right">
                                    <a className="mr-20" href={baseUrl + shortenURL.slug} target="_blank">{baseUrl}{shortenURL.slug}</a>
                                    <button className="btn btn-primary">Copy</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div >
    )
}

export default UrlSorten;
