import { React, useState } from 'react';
import axios from 'axios';
import ShortenUrlHelpers from './ShortenUrlHelper.js';

const UrlSorten = () => {
    const [shortenURLs, setShortenURLs] = useState(ShortenUrlHelpers.getShortenUrl());
    const [inputText, setInput] = useState(''); // '' is the initial state value
    const [errMsg, setErrMsg] = useState('');
    const [isCopy, setCopy] = useState('');

    const getShortenUrl = SortenUrlHelpers

    const handelSortenUrl = (e) => {
        const url = inputText;
        if (!url) {
            setErrMsg("Please, provide a valid url");
            destroyNotification(true, setErrMsg);
        } else {
            axios.post('http://localhost:4000/generateSortenUrl', { url }).then(res => {
                console.log("res", res);
                const { slug } = res.data;
                if (slug) {
                    setInput('http://localhost:4000/' + slug);
                    setCopy(true);
                }
            });
        }
        //'https://www.insightmetricsbd1.com/';
    }

    const destroyNotification = (isError, setErrMsg) => {
        console.log("errMsg", errMsg, "setErrMsg", setErrMsg);
        if (isError) {
            setTimeout(function () {
                setErrMsg('');
            }, 2000)
        }
    };

    return (
        <div className="container">
            <div><h1 className="page-title">URL Sorten</h1></div>
            <div className="form-row align-items-center">
                <div className="col-auto">
                    <input type="text" className="form-control sorten-link" placeholder="Shorten your link" onInput={e => setInput(e.target.value)} value={inputText} />
                    {isCopy ? (
                        <button className="btn btn-success" onClick={handelSortenUrl}>Copy</button>
                    ) : (
                        <button className="btn btn-success" onClick={handelSortenUrl}>Sorten</button>
                    )}
                </div>
                {errMsg &&
                    <div className="alert alert-danger mt-20" role="alert">
                        {errMsg}
                    </div>
                }
                <div className="card mt-20">
                    <div className="card-body">
                        hfkjldhfkdshf
                    </div>
                </div>
            </div>
        </div>
    )
}

export default UrlSorten
